def test_widget_options(module, property, expected_widget_options, load_modules):
    """Test to check widget options, including dependencies, subsyntaxes and expected widgets from syntaxes"""
    mod = modules.get(module)
    prop = mod.property_descriptions.get(property)
    s = prop.syntax
    widget_options = s.get_widget_options(prop)
    assert widget_options == expected_widget_options
